<?php	require_once("basicshapeabstract.class.php");	class Rect extends BasicShapeAbstract{				function __construct($itemId){			$this->id = $itemId;			$this->shapeType = "rect";		}				public function setCoordinates($x, $y){			$this->x = $x;			$this->y = $y;		}				public function setSize($width, $height){			$this->width = $width;			$this->height = $height;		}			}?>